$(window).on("load", function() {
    $(".sandytheme-related .label1 .row, .owl-stage-outer, .owl-dots, .owl-nav").css({
        opacity: "1"
    });
    $(".owl-carousel").removeClass("beforeload");
    $(".owl-item.active .item-owl .boxow p").addClass("activeeffect");
    $(".owl-carousel.slide1").on("change.owl.carousel", function(a) {
        $(".owl-carousel.slide1 .owl-item .item-owl .boxow p").addClass("activeeffect");
        $(".owl-carousel.slide1 .owl-item.active .item-owl .boxow p").removeClass("activeeffect")
    });
    $("ul.ct_tabs li").click(function() {
        var a =
            $(this).attr("data-tab");
        $("ul.ct_tabs li").removeClass("current");
        $(".tab-content").removeClass("current");
        $(this).addClass("current");
        $("#" + a).addClass("current")
    });


});
